inp_list=[]
with open("input.txt", "r") as f:
    for line in f.readlines():
        inp_list.append(line.strip())

len_voc = int(inp_list[0])

voc_d = dict()
for idx in range(1, len_voc+1):
    voc_word = inp_list[idx]
    voc_word_lower = voc_word.lower()
    if voc_word_lower not in voc_d:
        voc_d[voc_word_lower] = set()
    voc_d[voc_word_lower].add(voc_word)

text = inp_list[len_voc+1].split()

err_count = 0
for w in text:
    w_lower = w.lower()
    if w_lower in voc_d:
        if w not in voc_d[w_lower]:
            err_count += 1
    else:
        w_accent_count = 0
        for sym in w:
            if sym.isupper():
                w_accent_count += 1
        if w_accent_count != 1:
            err_count += 1

print(err_count)

